import { useState, useEffect } from "react";
import { NOMBRETIENDA , DB, COMETA } from "../CONST";

import "./Grilla.css";

function Grilla() {
	const [elproducto, setElproducto] = useState();
	const [loading, setLoading] = useState();
	
	useEffect(() => {
		fetch( DB ).then((db) => db.json()).then((db) => {
			const data = [];

			for ( let count = 1; count <= db.values.length - 1; count++ ) {
				const resumen = db.values[count];
				
				const p = {
					id: resumen[0],
					nombre: resumen[1].replaceAll("SHEIN ", ""),
					precio: (Number(resumen[2])+COMETA).toLocaleString("es-CL"),
					fechaentrega: resumen[3],
					imagen: resumen[4].split(",//")[0],
				};
				
				data.push({ id: p.id, nombre: p.nombre, precio: p.precio, fechaentrega: p.fechaentrega, imagen: p.imagen });
			};
			
			setElproducto(data);
			setLoading(false);
		});
	},[]);
	
	function Producto({ id, nombre, precio, fechaentrega, imagen }) {
		return (
			<a key={id} className="producto" href="/" title={nombre}>
				<div className="imagen">
					<img src={imagen} alt={NOMBRETIENDA} />
				</div>
				<h2>{nombre}</h2>
				<strong>$ {precio}</strong>
				<p className="fechaentrega">{fechaentrega}</p>
			</a>
		)
	}
	
	function Cargando () {
		return (<p>Cargando ...</p>)
	}

	return (
		<section id="grilla">
			{<Cargando /> ? "true, cargando ...": "false, cargado." }
				<a className="producto"></a>
				<p>&nbsp;</p>
				<p>&nbsp;</p>
				{elproducto?.map((prod) => {
					return (
						<Producto
							key={prod.id}
							id={prod.id}
							nombre={prod.nombre}
							precio={prod.precio}
							fechaentrega={prod.fechaentrega}
							imagen={prod.imagen}
						/>
					)
				})}
		</section>
	)
}
export default Grilla;
