import { useState, useEffect } from "react";
import { NOMBRETIENDA } from "../CONST";


function Grilla() {
	const Productos = function () {
		const data = {
			imagen: "https://clickzapatilla.cl/imagenes/productos/nike/airvapormaxtnplus/202209141351/30731578.jpg",
			marca: "nike",
			modelo: "air vapormax tn plus",
			precio: 74990,
			tallas: [39, 40, 41, 42, 43, 44, 45, 46, 47],
		}
		return(
			<section className="producto">
				<img src={data.imagen} alt={NOMBRETIENDA} />
				<strong>{data.modelo}</strong>
				<p>{data.marca}</p>
				<p>$ {data.precio.toLocaleString()}</p>
				<ul>
					<p>Tallas: </p>
					{
						data.tallas.map((talla) => (<li key={talla}>[{talla}]</li>))
					}
				</ul>
			</section>
		)
	};

	useEffect(() => {
		const api = "https://rickandmortyapi.com/api/character";
		fetch( api ).then(x => x.json()).then(x => {
	
		});
	},[]);

	return (
		<section id="grilla">
			<Productos />
		</section>
	)
}
export default Grilla;
