import { useState, useEffect } from "react";
import { NOMBRETIENDA } from "../CONST";


function Grilla() {
	const [elproducto, setElproducto] = useState();
	
	useEffect(() => {
		const api = "https://rickandmortyapi.com/api/character";
		
		fetch( api ).then((api) => api.json()).then((personajes) => {
			const data = [];
			for ( let count = 0; count <= personajes.results.length - 1; count++ ) {
				const resumen = personajes.results[count];
				
				const p = {
					id: resumen.id,
					nombre: resumen.name,
					imagen: resumen.imagen,
				};
				
				data.push({ id: p.id, nombre: p.nombre, imagen: p.imagen });
			};
			setElproducto(data);
		});
	},[]);
	
	function Producto( id, imagen, nombre ) {
		return (
			<section className="producto">
				<img src={imagen} alt={NOMBRETIENDA} />
				<h2>{nombre}</h2>
				<strong>hola</strong>
			</section>
		)
	}

	return (
		<section id="grilla">
			{elproducto?.map((prod) => {
				return (
					// <div key={prod.id}>
					// 	<p>{prod.imagen}</p>
					// 	<p>{prod.nombre}</p>
					// </div>
					<Producto id={prod.id} imagen={prod.imagen} nombre={prod.nombre} />
				)
			})}
		</section>
	)
}
export default Grilla;
