import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { DB, COMETA } from "../CONST";

function Detalle() {
	// console.log( lugar );
	const lugar = useLocation().pathname.split("detalle/")[1];
	const [detalle, setDetalle] = useState();
	
	useEffect(() => {
		fetch( DB ).then( x => x.json() ).then( x => {
			const resumen = x.values[lugar];
			const todo = [];
			const p = {
				id: resumen[0],
				nombre: resumen[1].replaceAll("SHEIN ", ""),
				precio: (Number(resumen[2])+COMETA).toLocaleString("es-CL"),
				fechaentrega: resumen[3],
				imagen: resumen[4].split(",//")[0],
			};
			// console.log( p.id );
			// console.log( p.nombre );
			// console.log( p );
			
			todo.push({ id: p.id, nombre: p.nombre, precio: p.precio });
			// console.log( todo );
			setDetalle( todo );
			// console.log(detalle[0].nombre);
		});
	},[]);
	
	// console.log( detalle[0].id );
	// console.log( detalle[0].nombre );
	
	return (
		<section id="detalle">
			{
				detalle?.map((resp) => {
					return(<p key={resp.id}>{resp.nombre}</p>)
				})
			}
			{/* <h1>{detalle[0].nombre}</h1>
			<img src={detalle[0].imagen} alt={detalle[0].nombre} />
			<h3>$ {detalle[0].precio}</h3> */}
		</section>
	)
}
export default Detalle;
