		// <>
		// 	{
		// 		detalle?.map((resp) => {
		// 			return (
		// 				<section key={resp.id} id="detalle">
		// 					<h2>{resp.nombre}</h2>
		// 					<img src={resp.imagen} alt={resp.nombre} />
		// 					<p className="precio">$ {resp.precio}</p>
		// 				</section>
		// 			)
		// 		})
		// 	}
		// </>
	// );
}
export default Detalle;
