import { useState, useEffect } from "react";
import { NOMBRETIENDA , DB } from "../CONST";

import ""

function Grilla() {
	const [elproducto, setElproducto] = useState();
	
	useEffect(() => {
		fetch( DB ).then((db) => db.json()).then((db) => {
			const data = [];

			for ( let count = 1; count <= db.values.length - 1; count++ ) {
				const resumen = db.values[count];
				
				const p = {
					id: resumen[0],
					nombre: resumen[1],
					precio: resumen[2],
					fechaentrega: resumen[3],
					imagen: resumen[4].split(",//")[0],
				};
				console.log( p.imagen );
				
				data.push({ id: p.id, nombre: p.nombre, imagen: p.imagen });
			};
			
			setElproducto(data);
		});
	},[]);
	
	function Producto({ id, imagen, nombre }) {
		return (
			<section key={id} className="producto">
				<img src={imagen} alt={NOMBRETIENDA} />
				<h2>{nombre}</h2>
			</section>
		)
	}

	return (
		<section id="grilla">
			{elproducto?.map((prod) => {
				return (
					<Producto
						id={prod.id}
						imagen={prod.imagen}
						nombre={prod.nombre} 
					/>
				)
			})}
		</section>
	)
}
export default Grilla;
