import { useState, useEffect } from "react";
import { NOMBRETIENDA , DB } from "../CONST";


function Grilla() {
	const [elproducto, setElproducto] = useState();
	
	useEffect(() => {
		// const api = "https://rickandmortyapi.com/api/character";
		
		fetch( DB ).then((db) => db.json()).then((db) => {
			console.log( db.values );
			const data = [];
			for ( let count = 0; count <= db.values.length - 1; count++ ) {
				const resumen = db.values[count];
				
				const p = {
					id: resumen.id,
					nombre: resumen.name,
					imagen: resumen.image,
				};
				
				data.push({ id: p.id, nombre: p.nombre, imagen: p.imagen });
			};
			setElproducto(data);
		});
	},[]);
	
	function Producto({ id, imagen, nombre }) {
		return (
			<section key={id} className="producto">
				<img src={imagen} alt={NOMBRETIENDA} />
				<h2>{nombre}</h2>
			</section>
		)
	}

	return (
		<section id="grilla">
			{elproducto?.map((prod) => {
				return (
					<Producto id={prod.id} imagen={prod.imagen} nombre={prod.nombre} />
				)
			})}
		</section>
	)
}
export default Grilla;
