import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import { DB, COMETA } from "../CONST";

import "./Detalle.css";

function Detalle() {
	let lugar = useLocation().pathname.split("detalle/")[1];
	lugar = lugar.replaceAll("%20", " ").replaceAll("%C3", "").replaceAll("%B3", "o").replaceAll("%A9", "e");
	console.log( lugar );
	const [detalle, setDetalle] = useState();
	
	useEffect(() => {
		fetch( DB ).then( x => x.json() ).then( x => {
			for ( let count = 1; count <= x.values.length-1; count++ ) {
				if ( x.values[count][1].includes(lugar) ) {
					console.log( `MATCH!` );
				} else {
					console.log("NO");
				}
			}
			// const resumen = x.values[lugar];
			// const todo = [];
			// const p = {
			// 	id: resumen[0],
			// 	nombre: resumen[1].replaceAll("SHEIN ", ""),
			// 	precio: (Number(resumen[2])+COMETA).toLocaleString("es-CL"),
			// 	fechaentrega: resumen[3],
			// 	imagen: resumen[4].split(",//")[0],
			// };
			
			// todo.push({ id: p.id, nombre: p.nombre, precio: p.precio, fechaentrega: p.fechaentrega, imagen: p.imagen });
			// setDetalle( todo );
		});
	},[lugar]);
	
	return (
		<>
		{
			detalle?.map((resp) => {
				return(
					<section key={resp.id} id="detalle">
						<h2>{resp.nombre}</h2>
						<img src={resp.imagen} alt={resp.nombre} />
						<p className="precio">$ {resp.precio}</p>
					</section>
				)
			})
		}
		</>
	)
}
export default Detalle;
